var db = require('mongojs')('test', ['myCollection']);

db.myCollection.save({t:242442, online:1});

db.myCollection.group({
    key: {},
    cond: {t: {$gte: 86400}},
    initial: {count: 0, online: 0},
    reduce: function(doc, out) {
        out.count++;
        out.online += doc.online;
    },
    finalize: function(out) {
        out.avgOnline = out.online / out.count;
    }
}, function(err, curOnline) {
    console.log(curOnline);
});